mapper.update:
UPDATE PLUS_TIME 
SET TITLE=#{timeTitle}, TIME=#{time} 
WHERE PTIME_NO=#{plusTimeNo}

UPDATE PLUS_TIME
SET COMMENTS = #{timeComments}
WHERE PTIME_NO = #{plusTimeNo}

UPDATE PLUS_SCORE
SET TITLE=#{scoreTitle}, SCORENO = #{scoreNo}
WHERE PSCORE_NO = #{plusScoreNo}

UPDATE PLUS_SCORE
SET COMMENTS = #{scoreComments}
WHERE PSCORE_NO = #{plusScoreNo}
