mapper.select:
SELECT CSTICKER_NO AS STICKERNO, CONTENT
FROM STICKER
WHERE AC_NO=#{acNo}

SELECT CH_B_NO AS CHBNO
FROM CHECK_BOOKMARK
WHERE AC_NO=#{acNo} AND CHECK_NO=#{checkNo}

SELECT CSTICKER_NO
FROM MYCHECKTAG
WHERE MC_TAG_NO=#{tagNo}

SELECT CSTICKER_NO
FROM CHECKTAG
WHERE CH_TAG_NO=#{tagNo}

SELECT CSTICKER_NO
FROM MYLOCALTAG
WHERE LO_TAG_NO=#{tagNo}
