mapper.select:
SELECT DONG_NO, DONGNAME, GU_NO
FROM DONG
WHERE GU_NO = #{guNo}

SELECT AC_NO, TYPE, MARKNO, LISTNO, GUNAME, DONGNAME, TITLE
     , DEPOSIT, WOLSE, JEONSE, MAEMAE, SEC, TP, HJ, CONVENIENCE_TOT, PET
FROM VIEW_LIST                                                          
WHERE AC_NO = #{acNo}
  AND TYPE LIKE #{type}
  AND GU_NO = #{guNo}
  AND DONG_NO = #{dongNo}
  AND CSTICKER_NO = #{stickerNo}

SELECT AC_NO, TYPE, MARKNO, LISTNO, GUNAME, DONGNAME, TITLE
     , DEPOSIT, WOLSE, JEONSE, MAEMAE, SEC, TP, HJ, CONVENIENCE_TOT, PET
FROM VIEW_LIST                                                          
WHERE AC_NO = #{acNo}
  AND TYPE LIKE #{type}
  AND GU_NO = #{guNo}
  AND CSTICKER_NO = #{stickerNo}

SELECT AC_NO, TYPE, MARKNO, LISTNO, GUNAME, DONGNAME, TITLE
     , DEPOSIT, WOLSE, JEONSE, MAEMAE, SEC, TP, HJ, CONVENIENCE_TOT, PET
FROM VIEW_LIST                                                          
WHERE AC_NO = #{acNo}
  AND TYPE LIKE #{type}
  AND CSTICKER_NO = #{stickerNo}

SELECT CSTICKER_NO, CONTENT
FROM VIEW_MYCHECKSTICKER
WHERE MARKNO = #{no}

SELECT CSTICKER_NO, CONTENT
FROM VIEW_CHECKSTICKER
WHERE MARKNO = #{no}

SELECT CSTICKER_NO, CONTENT
FROM VIEW_LOCALSTICKER
WHERE MARKNO = #{no}
